import React from 'react';
import { Link } from 'react-router-dom';
import BedroomParentIcon from '@mui/icons-material/BedroomParent';
import BathtubIcon from '@mui/icons-material/Bathtub';
import LocalParkingIcon from '@mui/icons-material/LocalParking';
import api from '../api';

const formatCurrency = (price) => {
  return new Intl.NumberFormat('en-US', { style: 'currency', currency: 'USD' }).format(price);
};

const PropertyPrice = ({ price }) => {
  return (
    <p className="mt-4 text-lg font-semibold text-gray-900">
      {formatCurrency(price)}
    </p>
  );
};

const PropertyCard = ({ property }) => {
  const { _id, images, title, description, bedrooms, bathrooms, size, address, availability, price } = property;

useEffect(() => {
  const fetchProperties = async () => {
    try {
      const response = await axiosInstance.get('/properties');
      setProperties(response.data);
    } catch (error) {
      console.error('Error fetching properties', error);
    }
  };

  fetchProperties();
}, []);

  return (
    <Link to={`/property/buy/${_id.$oid}`} className="block rounded-lg shadow-lg overflow-hidden">
      {images.length > 0 && (
        <img src={images[0]} alt={title} className="w-full h-48 object-cover" />
      )}
      <div className="p-4">
        <h3 className="text-lg font-semibold text-gray-900">{title}</h3>
        <div className="h-24 overflow-hidden">
          <p className="mt-2 text-gray-600">{description.substring(0, 80)}...</p>
        </div>
        <div className="mt-4 flex items-center">
          <div className="flex items-center mr-4">
            <BedroomParentIcon className="w-5 h-5 text-gray-500" />
            <span className="ml-1 text-gray-700">{bedrooms}</span>
          </div>
          <div className="flex items-center mr-4">
            <BathtubIcon className="w-5 h-5 text-gray-500" />
            <span className="ml-1 text-gray-700">{bathrooms}</span>
          </div>
          <div className="flex items-center mr-4">
            <span className="text-gray-700">{size} sq ft</span>
          </div>
          {availability && (
            <div className="flex items-center">
              <span className="bg-green-500 rounded-full px-3 py-1 text-sm font-semibold text-white">
                Available
              </span>
            </div>
          )}
        </div>
        <PropertyPrice price={parseFloat(price.amount.$numberDecimal)} />
      </div>
    </Link>
  );
};

export default PropertyCard;
