//api.js
// api.js
import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:3000/api', // Ajusta la URL base si es necesario
});

// Interceptores para manejar tokens de autenticaci贸n (JWT)
api.interceptors.request.use(
  (config) => {
    const accessToken = localStorage.getItem('accessToken'); // Obtiene el token del localStorage
    if (accessToken) {
      config.headers.Authorization = `Bearer ${accessToken}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Interceptores para manejar respuestas 401 (No autorizado)
api.interceptors.response.use(
  (response) => {
    return response;
  },
  async (error) => {
    const originalRequest = error.config;
    if (error.response.status === 401 && !originalRequest._retry) {
      originalRequest._retry = true;
      try {
        const refreshToken = localStorage.getItem('refreshToken');
        if (!refreshToken) {
          // Si no hay refreshToken, redirige al login
          window.location.href = '/login';
          return Promise.reject(error);
        }
        const response = await axios.post('http://localhost:3000/api/auth/refresh-token', { refreshToken }); // Ajusta la URL si es necesario
        const { accessToken } = response.data;
        localStorage.setItem('accessToken', accessToken);
        originalRequest.headers.Authorization = `Bearer ${accessToken}`;
        return api(originalRequest);
      } catch (refreshError) {
        // Si falla la renovaci贸n del token, redirige al login
        window.location.href = '/login';
        return Promise.reject(refreshError);
      }
    }
    return Promise.reject(error);
  }
);

export default api;
