import React from 'react';
import { Typography, Grid, Box, Button } from '@mui/material';
import HomeIcon from '@mui/icons-material/Home';
import BathroomIcon from '@mui/icons-material/Bathroom';
import LocalParkingIcon from '@mui/icons-material/LocalParking';
import { Link } from 'react-router-dom';

const PropertyDetailsBuy = ({ property }) => {
  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" gutterBottom>
        {property.location.address}
      </Typography>
      <Grid container spacing={3}>
        <Grid item xs={12} md={6}>
          <img src={property.images[0]} alt={property.location.address} style={{ width: '100%' }} />
        </Grid>
        <Grid item xs={12} md={6}>
          <Typography variant="body1" gutterBottom>
            {property.description}
          </Typography>
          <Box sx={{ mt: 2 }}>
            <Grid container spacing={1} alignItems="center">
              <Grid item>
                <HomeIcon /> {property.bedrooms}
              </Grid>
              <Grid item>
                <BathroomIcon /> {property.bathrooms}
              </Grid>
              {property.parking && (
                <Grid item>
                  <LocalParkingIcon />
                </Grid>
              )}
            </Grid>
          </Box>
          <Typography variant="h6" sx={{ mt: 2 }}>
            ${property.price}
          </Typography>
          <Button variant="contained" sx={{ mt: 2 }}>
            Hacer una oferta
          </Button>
          <Button component={Link} to={`/finance?propertyId=${property._id}`} variant="contained" sx={{ mt: 2, ml: 2 }}>
            Financiar Propiedad
          </Button>
        </Grid>
      </Grid>
    </Box>
  );
};

export default PropertyDetailsBuy;
