import React, { useState } from 'react';
import HomeIcon from '@mui/icons-material/Home';
import BathroomIcon from '@mui/icons-material/Bathroom';
import LocalParkingIcon from '@mui/icons-material/LocalParking';

const formatCurrency = (price) => {
  return new Intl.NumberFormat('en-US', { style: 'currency', currency: 'USD' }).format(price);
};

const PropertyDetailsRent = ({ property }) => {
  const [startDate, setStartDate] = useState('');
  const [months, setMonths] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    // Aquí puedes implementar la lógica para enviar la solicitud de renta
    console.log('Solicitud de renta:', { propertyId: property._id.$oid, startDate, months });
  };

  const {
    _id,
    title,
    description,
    bedrooms,
    bathrooms,
    size,
    address,
    availability,
    price,
    images
  } = property;

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-3xl font-bold mb-4">{title}</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <div>
          {images.length > 0 && (
            <img src={images[0]} alt={address.fullAddress} className="w-full h-96 object-cover" />
          )}
        </div>
        <div>
          <p className="text-gray-700">{description}</p>
          <div className="mt-4 flex items-center">
            <div className="flex items-center mr-4">
              <HomeIcon className="w-5 h-5 text-gray-500" />
              <span className="ml-1 text-gray-700">{bedrooms} Bedrooms</span>
            </div>
            <div className="flex items-center mr-4">
              <BathroomIcon className="w-5 h-5 text-gray-500" />
              <span className="ml-1 text-gray-700">{bathrooms} Bathrooms</span>
            </div>
            <div className="flex items-center mr-4">
              <span className="text-gray-700">{size} sq ft</span>
            </div>
            {availability && (
              <div className="flex items-center">
                <span className="bg-green-500 rounded-full px-3 py-1 text-sm font-semibold text-white">
                  Available
                </span>
              </div>
            )}
          </div>
          <p className="mt-4 text-2xl font-bold text-gray-900">{formatCurrency(parseFloat(price.amount.$numberDecimal))}</p>
          <form onSubmit={handleSubmit} className="mt-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <input 
                type="date" 
                value={startDate} 
                onChange={(e) => setStartDate(e.target.value)} 
                className="border border-gray-300 px-3 py-2 rounded focus:outline-none focus:ring-2 focus:ring-blue-500" 
              />
              <input 
                type="number" 
                value={months} 
                onChange={(e) => setMonths(e.target.value)} 
                placeholder="Meses" 
                className="border border-gray-300 px-3 py-2 rounded focus:outline-none focus:ring-2 focus:ring-blue-500" 
              />
            </div>
            <button type="submit" className="mt-4 bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
              Solicitar Renta
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default PropertyDetailsRent;
